typedef vector<vector<int>> mat;

const int INF = 1 << 28;
int n, m;

bool check(mat &v) {
    if (v[0].size() == n + 1 || v[2].size() == n + 1)
        return true;
    return false;
}

string stom(mat &v) {
    string res;
    res = "";
    for (int i = 0; i < 3; i++) {
        for (auto j : v[i]) {
            if (j < 0) continue;
            res = res + (char)('a' + j);
        }
        for (int j = v[i].size(); j <= n; j++) {
            res = res + '0';
        }
    }
    return res;
}

set<string> M;

void hoge(mat &v, vector<mat> &next) {
    if (M.find(stom(v)) != M.end()) return;
    M.insert(stom(v));
    for (int i = 0; i < 3; i++) {
        if (v[i].size() > 1) {
            for (int j = 0; j < 3; j++) {
                if (i == j || v[i][v[i].size() - 1] < v[j][v[j].size() - 1]) continue;
                if (i == 0 && j == 2) continue;
                if (i == 2 && j == 0) continue;
                v[j].push_back(v[i][v[i].size() - 1]);
                v[i].erase(v[i].end() - 1);
                next.push_back(v);
                v[i].push_back(v[j][v[j].size() - 1]);
                v[j].erase(v[j].end() - 1);
            }
        }
    }
}


int main() {
    while (cin >> n >> m, n || m) {
        mat v(3);
        for (int i = 0; i < 3; i++) {
            int n_; cin >> n_;
            v[i].push_back(-1);
            for (int j = 0; j < n_; j++) {
                int x; cin >> x;
                v[i].push_back(x);
            }
        }

        int res = 0;
        vector<mat> G;
        G.push_back(v);
        while (res <= m) {
            vector<mat> G_(G);
            G.resize(0);
            for (auto g : G_) {
                if (check(g)) {
                    goto escap;
                }
                hoge(g, G);
            }
            res++;
        }
escap:
        cout << (res > m ? -1 : res) << endl;
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int inf = 1001001001;

int n, m;
stack<int> t[3];

int dfs(int depth = 0) {
  if (max(t[0].size(), t[2].size()) == n + 1) return 0;
  if (depth == m) return inf;

  int ret = inf;

  if (t[0].top() < t[1].top()) {
    t[0].push(t[1].top());
    t[1].pop();

    ret = min(ret, dfs(depth + 1) + 1);

    t[1].push(t[0].top());
    t[0].pop();
  }
  if (t[0].top() > t[1].top()) {
    t[1].push(t[0].top());
    t[0].pop();

    ret = min(ret, dfs(depth + 1) + 1);

    t[0].push(t[1].top());
    t[1].pop();
  }

  if (t[2].top() < t[1].top()) {
    t[2].push(t[1].top());
    t[1].pop();

    ret = min(ret, dfs(depth + 1) + 1);

    t[1].push(t[2].top());
    t[2].pop();
  }
  if (t[2].top() > t[1].top()) {
    t[1].push(t[2].top());
    t[2].pop();

    ret = min(ret, dfs(depth + 1) + 1);

    t[2].push(t[1].top());
    t[1].pop();
  }

  return ret;
}

