#include <cmath>
#include <iostream>
#include <queue>
#include <vector>

using namespace std;

struct State {
  vector<int> S[3];
  int step;
  int parent_from;
  int parent_to;

  bool empty(size_t i) const { return S[i].size() == 0; }
  void push(size_t i, int v) { S[i].push_back(v); }
  void pop(size_t i) { S[i].pop_back(); }
  int top(size_t i) const { return S[i].back(); }
};

bool Done(const State& s, const State& goal) {
  for (int i = 0; i <= 2; ++i) {
    if (s.S[i].size() != goal.S[i].size()) return false;
    if (s.S[i] != goal.S[i])
      return false;
  }
  return true;
}

inline bool IsMovable(int from, int to, const State& p) {
  if (to < 0 || to >= 3) return false;
  if (p.empty(from)) return false;
  if (to == p.parent_from && from == p.parent_to) return false;
  return p.empty(to) || p.top(from) > p.top(to);
}

int BFS(const State& start, const State& goal) {
  queue<State> q;
  q.push(start);

  int step = 0;
  while (!q.empty()) {
    const auto p = q.front();
    q.pop();

    if (Done(p, goal)) {
      step = p.step;
      break;
    }
    for (int from = 0; from < 3; ++from) {
      for (int d : {-1, 1}) {
        const int to = from + d;
        if (IsMovable(from, to, p)) {
          State new_state = p;
          new_state.step = p.step + 1;
          new_state.parent_from = from;
          new_state.parent_to = to;
          new_state.push(to, p.top(from));
          new_state.pop(from);
          q.push(new_state);
        }
      }
    }
  }
  return step;
}

int main() {
  cin.tie(0);
  ios::sync_with_stdio(false);

  int n, m;
  for (;;) {
    cin >> n >> m;
    if (n == 0 && m == 0) break;

    // 
    State start;
    start.step = 0;
    start.parent_from = -1;
    start.parent_to = -1;
    for (int i = 0; i < 3; ++i) {
      int t;
      cin >> t;
      int k;
      for (int j = 0; j < t; ++j) {
        cin >> k;
        start.S[i].push_back(k);
      }
    }
    // C
    State goal;
    goal.step = 0;
    goal.parent_from = -1;
    goal.parent_to = -1;
    for (int i = 1; i <= n; ++i) {
      goal.S[2].push_back(i);
    }

    const int N = pow(3, n) - 1;
    const int step = BFS(goal, start);
    const int ans = min(step, N - step);
    if (ans <= m)
      cout << ans << endl;
    else
      cout << "-1" << endl;
  }
  return 0;
}

