int func(int cnt,string p){

  if(cnt>m)return INT_MAX;

  if(B.empty() && (C.empty()||A.empty()))
    return cnt;

  int res=INT_MAX;

  if(!A.empty() && A.top()>=B.top() && p!="BA"){
    B.push(A.top());
    if(!A.empty())A.pop();
    res=min(res,func(cnt+1,"AB"));
    A.push(B.top());
    if(!B.empty())B.pop();
  }

  if(!B.empty()){
    if(B.top()>=C.top() && p!="CB"){
      C.push(B.top());
      if(!B.empty())B.pop();
      res=min(res,func(cnt+1,"BC"));
      B.push(C.top());
      if(!C.empty())C.pop();
    }

    if(B.top()>=A.top() && p!="AB"){
      A.push(B.top());
      if(!B.empty())B.pop();
      res=min(res,func(cnt+1,"BA"));
      B.push(A.top());
      if(!A.empty())A.pop();
    }
  }

  if(!C.empty() && C.top()>=B.top() && p!="BC"){
    B.push(C.top());
    if(!C.empty())C.pop();
    res=min(res,func(cnt+1,"CB"));
    C.push(B.top());
    if(!B.empty())B.pop();
  }

  return res;
}

