		FOR_LT(i, 0, n_a) {
			// do nothing
			int a; cin >> a;
		}

		int n_b; cin >> n_b;
		FOR_LT(i, 0, n_b) {
			int b; cin >> b;
			b--;
			init_state |= (1 << (b * 2));
		}

		int n_c; cin >> n_c;
		FOR_LT(i, 0, n_c) {
			int c; cin >> c;
			c--;
			init_state |= (2 << (c * 2));
		}
	}

	unordered_set<uint32_t> searched;

	priority_queue < Search, vector<Search>, function<bool(const Search&, const Search&)>>
		search([](const auto & lhs, const auto & rhs) { return lhs.val > rhs.val; });
	search.push({ 0, init_state });
	searched.insert(init_state);

	while (!search.empty()) {
		const auto cur = search.top();
		search.pop();

		if (m < cur.val) {
			cout << -1 << endl;
			return;
		}

		if (is_goal(n, cur.state)) {
			cout << cur.val << endl;
			return;
		}

		int has_top_a = 0;
		int has_top_b = 0;
		int has_top_c = 0;
		FOR_DO(i, n - 1, 0) {
			uint32_t s = ((cur.state) >> (i * 2)) & 3;

			if (!has_top_a && s == 0) {
				if (!has_top_b) {
					uint32_t next = cur.state;
					next ^= (1 << (i * 2));

					add(search, searched, { cur.val + 1, next});
				}
				has_top_a = 1;
			}
			if (!has_top_b && s == 1) {
				if (!has_top_a) {
					uint32_t next = cur.state;
					next ^= (1 << (i * 2));

					add(search, searched, { cur.val + 1, next });
				}
				if (!has_top_c) {
					uint32_t next = cur.state;
					next ^= (3 << (i * 2));

					add(search, searched, { cur.val + 1, next });
				}
				has_top_b = 1;
			}
			if (!has_top_c && s == 2) {
				if (!has_top_b) {
					uint32_t next = cur.state;
					next ^= (3 << (i * 2));

					add(search, searched, { cur.val + 1, next });
				}
				has_top_c = 1;
			}
			if (has_top_a + has_top_b + has_top_c == 2) {
				break;
			}
		}
	}

	assert(!"error");
}

int main()
{
	while (true) {
		int n, m; cin >> n >> m;
		if (n == 0 && m == 0) {
			return 0;
		}

		solve(n, m);
	}

	return 0;
}

