using std::min;

constexpr long fmove(int n)
{
  return (n == 0 ? 0
          : n == 1 ? 1
          : fmove(n - 1) * 3 + 1);
}

static const long fmove_table[15] = {
  fmove(0), fmove(1), fmove(2), fmove(3), fmove(4),
  fmove(5), fmove(6), fmove(7), fmove(8), fmove(9),
  fmove(10), fmove(11), fmove(12), fmove(13), fmove(14),
};

static long
move_full(int n)
{
  return fmove_table[n];
}

static int
opposite_side(int here)
{
  return here ^ 2;
}

static long
move_all_to(int N, int M, char table[15], int base, int dest)
{
  if (base == N)
  {
    return 0;
  }
  int src = table[base];
  return (dest == src ? move_all_to(N, M, table, base + 1, dest)
          : src == 1 ? (move_all_to(N, M, table, base + 1, opposite_side(dest))
                        + 1 + move_full(N - (base + 1)) * 2)
          : dest == 1 ? (move_all_to(N, M, table, base + 1, opposite_side(src))
                         + 1 + move_full(N - (base + 1)))
          : (move_all_to(N, M, table, base + 1, dest)
             + 2 + move_full(N - (base + 1)) * 4));
}

static int
solve(int N, int M, char table[15])
{
  long count = min(move_all_to(N, M, table, 0, 0),
                   move_all_to(N, M, table, 0, 2));
  return count <= M ? int(count) : -1;
}

