#define REP(i,b,n) for(int i=b;i<n;i++)
#define rep(i,n) REP(i,0,n)
#define IEP(i,b,n) for(int i=b;i>=n;i--)
#define iep(i,b) IEP(i,b,0)

int n, m, tray[3][20], tray_size[3], min_turn;

// put a to b
void doPut(int a, int b) {
  int rec = tray[a][tray_size[a]-1];
  tray[a][tray_size[a]-1] = 0;
  tray_size[a]--;
  tray[b][tray_size[b]] = rec;
  tray_size[b]++;
}
// back from b to a
void doBack(int a, int b) {
  int rec = tray[b][tray_size[b]-1];
  tray[b][tray_size[b]-1] = 0;
  tray_size[b]--;
  tray[a][tray_size[a]] = rec;
  tray_size[a]++;
}

void dfs(int turn) {
  if (turn > min_turn) return;
  rep(i,3) if (i != 1 && tray_size[i] == n) {
    /*
    cout<<"Turn #"<<turn<<endl;
    rep(z,n) {
      rep(zz,tray_size[z]) cout<<tray[z][zz]<<" ";
      cout<<endl;
    }
    cout<<"+++++"<<endl;
    */
    min_turn = turn; return;
  }

  rep(i,3) {
    if (tray_size[i] == 0) continue;

    int rec[3];
    rep(j,3) rec[j] = (tray_size[j]!=0)? tray[j][tray_size[j]-1] : 0 ;

    if (i == 0) {
      if (rec[0] < rec[1]) continue;      
      doPut(0, 1);
      dfs(turn+1);
      doBack(0, 1);
    } else if (i == 1) {
      if (rec[1] > rec[0]) {
        doPut(1, 0);
        dfs(turn+1);
        doBack(1, 0);
      }
      if (rec[1] > rec[2]) {
        doPut(1, 2);
        dfs(turn+1);
        doBack(1, 2);
      }
    } else if (i == 2) {
      if (rec[2] < rec[1]) continue;
      doPut(2, 1);
      dfs(turn+1);
      doBack(2, 1);
    }
  }
}

void solve() {
  memset(tray, 0, sizeof tray);
  min_turn = m+1;
  rep(i,3) {
    cin>>tray_size[i];
    rep(j,tray_size[i]) cin>>tray[i][j];
  }

  dfs(0);
  if (min_turn == m+1) min_turn = -1;
  cout<<min_turn<<endl;
}

int main (int argc, char const* argv[]) {
  while (cin>>n>>m, n||m) {
    solve();
  }
  return 0;
}

