#define REP(i,n) for(int i=0;i<(int)n;++i)
#define FOR(i,c) for(__typeof((c).begin())i=(c).begin();i!=(c).end();++i)
#define ALL(c) (c).begin(), (c).end()
const int INF = 1<<29;

int main() {
  int hanoi[16][3][3];
  REP(j,3) REP(k,3) hanoi[0][j][k] = 0;
  REP(i,15) {
    REP(j,3) {
      REP(k,3) {
        if (k==j) {
          hanoi[i+1][j][k] = 0;
          continue;
        }
        if (abs(k-j)==2) {
          hanoi[i+1][j][k] = hanoi[i][j][k] + 1 + hanoi[i][k][j] + 1 + hanoi[i][j][k];
          continue;
        }
        bool hoge[3] ={};
        hoge[j] = hoge[k] = 1;
        int a;
        REP(l,3) if (!hoge[l]) a = l;
        hanoi[i+1][j][k] = hanoi[i][j][a] + 1 + hanoi[i][a][k];
        //      printf("%d,%d,%d %d\n",i,j,k,hanoi[i][j][k]);
      }
    }
  }
//  cout << "OK" << endl;

  int n, m;
  while(cin>>n>>m, n||m) {
    typedef pair<int, int> pii;
    vector<pii> v; // size, pos
    REP(i, 3) {
      int a;
      cin >> a;
      REP(j, a) {
        int size;
        cin >> size;
        v.push_back(pii(size,i));
      }
    }
    sort(ALL(v), greater<pii>());

    int dp[n][3];               // dp[i][j] : i Jbvj 
    // REP(i,m+1) REP(j,3)
    //   dp[i][j] = INF;
    REP(i, 3) dp[0][i] = 0;

    REP(i, n) {
      REP(j, 3) {
//        printf("i=%d, j=%d\n",i,j);
        int aa = v[i].second;
        if (aa==j) {
          dp[i+1][j] = dp[i][j];
          continue;
        }
        if (abs(aa-j)==2) {
          dp[i+1][j] = dp[i][j] + 1 + hanoi[i][j][aa] + 1 + hanoi[i][aa][j];
          continue;
        }

        bool hoge[3] ={};
        hoge[j] = hoge[aa] = 1;
        int a;
        REP(l,3) if (!hoge[l]) a = l;
        dp[i+1][j] = dp[i][a] + abs(aa-j) + hanoi[i][a][j];
      }
    }

    int res = min(dp[n][0], dp[n][2]);
    if (res<=m) cout << res << endl;
    else cout << -1 << endl;
  }
}

