using namespace std;

const int DELTA[] = {-1, 1};

struct Hash {
    size_t operator()(const vector<int> &v) const {
        size_t val = 0;
        for(vector<int>::const_iterator it = v.begin(); it != v.end(); ++it) {
            val <<= 2;
            val += *it;
        }
        return val;
    }
};

bool all_a(const vector<int> &v, int val) {
    for(vector<int>::const_iterator it = v.begin(); it != v.end(); ++it) {
        if(*it != val) return false;
    }
    return true;
}

int main() {
    while(true) {
        int N, M;
        cin >> N >> M;
        if(!N && !M) break;

        vector<int> state(N);
        for(int i = 0; i < 3; ++i) {
            int n;
            cin >> n;
            while(n--) {
                int idx;
                cin >> idx;
                state[idx-1] = i;
            }
        }

        tr1::unordered_set<vector<int>,Hash> visited;
        queue<pair<int, vector<int> > > q;
        q.push(make_pair(0, state));
        visited.insert(state);
        int ans = -1;
        while(!q.empty()) {
            int turn = q.front().first;
            vector<int> cur = q.front().second;
            /*
            cout << "(" << turn << ") ";
            for(int i = 0; i < cur.size(); ++i) {
                cout << cur[i] << ' ';
            }
            cout << endl;
            */
            q.pop();
            if(all_a(cur, 0) || all_a(cur, 2)) {
                ans = turn;
                break;
            }
            if(turn == M) continue;

            int top_of[3] = {-1, -1, -1};
            for(int i = N-1; i >= 0; --i) {
                const int pos = cur[i];
                if(top_of[pos] == -1) {
                    top_of[pos] = i;
                    for(int j = 0; j < 2; ++j) {
                        const int to = pos + DELTA[j];
                        if(to < 0 || to >= 3) continue;
                        if(top_of[to] < i) {
                            vector<int> next = cur;
                            next[i] = to;
                            if(visited.count(next)) continue;
                            /*
                            cout << "yield ";
                            for(int k = 0; k < cur.size(); ++k) {
                                cout << next[k] << ' ';
                            }
                            cout << endl;
                            */
                            visited.insert(next);
                            q.push(make_pair(turn+1, next));
                        }
                    }
                }
            }
        }

        cout << ans << endl;
    }
    return 0;
}

