stack<int> s[3];

int dfs(int i, int t) 
{
    if (t > m) return -1;
    if (s[0].size() == n || s[2].size() == n) return t;
    int x, y;
    x = s[i].empty() ? 0 : s[i].top();
    y = s[1].empty() ? 0 : s[1].top();    
    if (x < y) {
        s[i].push(y);
        s[1].pop();
    }
    else {
        s[1].push(x);
        s[i].pop();
    }
    return dfs((i + 2) % 4, t + 1);
}

void init_stack() 
{
    REP(i, 3) s[i] = stack<int>();
    REP(i, 3) REP(j, k[i]) s[i].push(c[i][j]);    
