void move(stack<int> *A, stack<int> *B, stack<int> *C, int count, int exp){
	int temp;
	if(count>maxcount)return;
	if((A->empty()||C->empty())&&B->empty()){
		if(count<mincount)mincount=count;
	}
	if(exp!=1&&((B->empty()&&A->empty()==false)||(B->empty()==false&&A->empty()==false&&A->top()>B->top()))){
		//0
		temp=A->top();
		A->pop();
		B->push(temp);
		count++;
		move(A,B,C,count,0);
		count--;
		B->pop();
		A->push(temp);
	}
	if(exp!=0&&((A->empty()&&B->empty()==false)||(A->empty()==false&&B->empty()==false&&B->top()>A->top()))){
		//1
		temp=B->top();
		B->pop();
		A->push(temp);
		count++;
		move(A,B,C,count,1);
		count--;
		A->pop();
		B->push(temp);
	}
	if(exp!=3&&((B->empty()&&C->empty()==false)||(B->empty()==false&&C->empty()==false&&C->top()>B->top()))){
		//2
		temp=C->top();
		C->pop();
		B->push(temp);
		count++;
		move(A,B,C,count,2);
		count--;
		B->pop();
		C->push(temp);
	}
	if(exp!=2&&((C->empty()&&B->empty()==false)||(C->empty()==false&&B->empty()==false&&B->top()>C->top()))){
		//3
		temp=B->top();
		B->pop();
		C->push(temp);
		count++;
		move(A,B,C,count,3);
		count--;
		C->pop();
		B->push(temp);
	}
}

