int main() {
	power[0] = 1;
	for (int i = 1; i < 16; i++) power[i] = power[i - 1] * 3;
	while (cin >> n >> m, n) {
		for (int i = 0; i < 3; i++) {
			cin >> c;
			for (int j = 0; j < c; j++) cin >> x, l[--x] = i;
		}
		int s = 0, f = 0;
		for (int i = 0; i < n; i++) s += power[i] * l[i];
		fill(d, d + power[n], -1); d[s] = 0;
		queue<int> que; que.push(s);
		while (!que.empty()) {
			int v = que.front(); que.pop();
			if (v == 0 || v == power[n] - 1) {
				cout << d[v] << endl; f = 1; break;
			}
			if (d[v] == m) continue;
			int maxr[3] = { -1, -1, -1 };
			for (int i = 0; i < n; i++) maxr[(v / power[i]) % 3] = i;
			for (int i = 0; i < 3; i++) {
				if (maxr[i] == -1) continue;
				int k = maxr[i];
				int e = (v / power[k]) % 3;
				for (int j = 0; j < 3; j++) {
					if (k < maxr[j]) continue;
					int nxt = -1;
					if (e - j == -1) nxt = v + power[k];
					if (e - j == 1) nxt = v - power[k];
					if (nxt != -1 && d[nxt] == -1) {
						d[nxt] = d[v] + 1;
						que.push(nxt);
					}
				}
			}
		}
		if (!f) cout << -1 << endl;
	}
}

