int T[20];
int A[20], B[20];
int n, m;

int solve() {
    int P[17];
    P[n] = 2;
    for ( int i = n; i > 0; -- i ) P[i-1] = 3 * P[i] + 2;

    A[n] = T[n];
    B[n] = 2 - T[n];
    for ( int i = n; i > 1; -- i ) {
        if ( T[i-1] == 0 ) {
            A[i-1] = A[i];
            B[i-1] = A[i] + 2 * P[i] + 2;
        } else if ( T[i-1] == 1 ) {
            A[i-1] = B[i] + P[i] + 1;
            B[i-1] = A[i] + P[i] + 1;
        } else {
            A[i-1] = A[i] + 2 * P[i] + 2;
            B[i-1] = B[i];
        }
    }
    int res = min( A[1], B[1] );
    if ( res > m ) return -1;
    return res;
}

int main() {
    while ( cin >> n >> m && n ) {
        for ( int i = 0; i < 3; ++ i ) {
            int k;
            cin >> k;
            for ( int j = 0; j < k; ++ j ) {
                int t;
                cin >> t;
                T[t] = i;
            }
        }

        cout << solve() << endl;
    }
    return 0;
}

