void debug(int pos, int n){
	rep(i, n){
		cout << pos % 3 << " ";
		pos /= 3;
	}
	cout << endl;
}

int main(){
	a[0] = 1;
	rep(i, 20) a[i + 1] = a[i] * 3;

	int n, m;
	while (cin >> n >> m, n | m){
		int start = 0;
		rep(i, 3){
			int k;
			cin >> k;
			while (k--){
				int x;
				cin >> x;
				start += i*a[x - 1];
			}
		}
		int goal = a[n] - 1;

		MEMSET(d, -1);
		d[start] = 0;
		queue<P> q;
		if (start == 0 || start == goal) goto OK;
		q.push(make_pair(start, 0));
		while (!q.empty()){
			P p = q.front();
			q.pop();

			int pos = p.first;
			int dist = p.second;

			int tmp = pos;
			bool ng[3] = {};
			for (int i = n - 1; i >= 0; --i){
				int x = tmp / a[i];
				tmp = tmp % a[i];
				if (ng[x]) continue;
				ng[x] = 1;
				for (int j = max(x - 1, 0); j <= min(x + 1, 2); ++j){
					if (ng[j]) continue;
					int nxt = pos + ((j - x)*a[i]);
					if (d[nxt] >= 0) continue;
					//debug(nxt, n);
					d[nxt] = dist + 1;
					if (nxt == 0 || nxt == goal) goto OK;
					if(dist + 1 < m) q.push(make_pair(nxt, dist+1));
				}
			}
		}
		cout << -1 << endl;
		continue;
	OK:;
		cout << max(d[0], d[goal]) << endl;
	}


	return 0;
}

