int n,m;

int main(){
	while(cin>>n>>m&&n){
		stack<int> A,B,C,X,Y,Z;
		int a,b,ans=INF,step=0;
		cin>>a;rep(i,a){cin>>b;X.push(b);}
		cin>>a;rep(i,a){cin>>b;Y.push(b);}
		cin>>a;rep(i,a){cin>>b;Z.push(b);}
		rep(i,2){
			if(i==0){A=X;B=Y;C=Z;}
			else{A=Z;B=Y;C=X;}
			for(;;){
				if(step==m+1||A.size()==n||C.size()==n)break;
				if(B.empty()||!A.empty()&&A.top()>B.top()){
					B.push(A.top());
					A.pop();
				}
				else{
					A.push(B.top());
					B.pop();
				}
				step++;
				if(step==m+1||A.size()==n||C.size()==n)break;
				if(C.empty()||!B.empty()&&B.top()>C.top()){
					C.push(B.top());
					B.pop();
				}
				else{
					B.push(C.top());
					C.pop();
				}
				step++;
			}
			ans=min(ans,step);
		}
		cout<<(ans<=m?ans:-1)<<endl;
	}
}

#include <stdio.h>
#include <stdbool.h>

#define INF 15
#define A 0
#define B 1
#define C 2

char cond[INF];
char n;
int  m;
int sum;

void move(int pos, char dist) {
    char now = cond[pos];

    if (now == dist) {
        return;
    } else if (abs(now-dist) == 2) {
        move(pos, B);
        move(pos, dist);
    } else {
        int i;
        char no_present = (dist+now == 1)?C:
                          (dist+now == 2)?B:
                                          A;
        for (i=1;pos+i < n;i++){
            if (cond[pos+i] == dist || cond[pos+i] == now) {
                move(pos+i, no_present);
            }
        }

        cond[pos] = dist;
        sum += 1;
    }

    return;
}

int main() {
    while (1) {
        int i;
        char dist;

        scanf("%d %d", &n, &m);
        if (n == 0 && m ==0) {
            break;
        }

        for (i=0;i < 3;i++) {
            int j,k;

            scanf("%d", &k);
            for (j=0;j < k;j++) {
                int tmp;

                scanf("%d", &tmp);
                cond[tmp-1] = i;
            }
        }

        dist = (cond[0] != B)?cond[0]:A;

        for (i=0;i < n;i++) {
            move(i, dist);
        }

        if (sum <= m) {
            printf("%d\n", sum);
        } else {
            printf("-1\n");
        }
    }
}

