    if(C&1)return move(A>>1,B>>1, C>>1);
    if(B&1)return move(C>>1,B>>1, A>>1)+move((A|B|C )>>1, 0, 0) + 1;
    if(A&1)return move(A>>1,B>>1, C>>1)+ 2*move((A|B|C)>>1, 0,0)+ 2;
}

