      memcpy(c,a,sizeof(c));
      memcpy(d,b,sizeof(d));
      mn=min(mn,fn(c,d,3));
    }else if(A(0)>A(1)&&A(1)<A(2)){
      mn=fn(c,d,0);
      memcpy(c,a,sizeof(c));
      memcpy(d,b,sizeof(d));
      mn=min(mn,fn(c,d,1));
    }else if(A(0)<A(1)&&A(1)<A(2)){
      mn=fn(c,d,1);
      memcpy(c,a,sizeof(c));
      memcpy(d,b,sizeof(d));
      mn=min(mn,fn(c,d,2));
    }else if(A(0)>A(1)&&A(1)>A(2)){
      mn=fn(c,d,0);
      memcpy(c,a,sizeof(c));
      memcpy(d,b,sizeof(d));
      mn=min(mn,fn(c,d,3));
    }
    if(mn==m+1)
      cout<<-1<<endl;
    else
      cout<<mn<<endl;
  }
  return 0;
}

