int n, m;
int put(int mask, int x, int i) {
    return (mask & ~(0b11 << 2 * x)) | (i << 2 * x);
}
int where(int mask, int x) {
    return (mask >> 2 * x) & 0b11;
}
int highest(int mask, int i) {
    int ans = 0;
    for(int x = 1; x <= n; x++) {
        ans = where(mask, x) == i ? x : ans;
    }
    return ans;
}
bool can(int mask, int from, int to) {
    return highest(mask, from) > highest(mask, to);
}
int shift(int mask, int from, int to) {
    return put(mask, highest(mask, from), to);
}
bool win(int mask) {
    return (highest(mask, 1) == n and not highest(mask, 2) and not highest(mask, 3))
        or (highest(mask, 3) == n and not highest(mask, 2) and not highest(mask, 1));
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    while(cin >> n >> m and n and m) {
        int start = 0;
        for(int i = 1; i <= 3; i++) {
            int sz;
            cin >> sz;
            while(sz--) {
                int x;
                cin >> x;
                start = put(start, x, i);
            }
        }
        queue<int> q({start});
        map<int, int> d = {{start, 0}};
        int ans = -1;
        while(not q.empty()) {
            int mask = q.front(); q.pop();
            if(win(mask)) {
                ans = d[mask] < m ? d[mask] : -1;
                break;
            }
            for(int i = 1; i <= 2; i++) {
                if(can(mask, i, i + 1) and not d.count(shift(mask, i, i + 1))) {
                    d[shift(mask, i, i + 1)] = d[mask] + 1;
                    q.push(shift(mask, i, i + 1));
                }
                if(can(mask, i + 1, i) and not d.count(shift(mask, i + 1, i))) {
                    d[shift(mask, i + 1, i)] = d[mask] + 1;
                    q.push(shift(mask, i + 1, i));
                }
            }
        }
        cout << ans << endl;
    }
    return 0;
}

