				next.A.push_back(next.B.back());
				next.B.pop_back();
				++next.step;
				que.push(next);
			}
			if (!now.B.empty() && (now.C.empty() || (now.B.back() > now.C.back()))){
				state next = now;
				next.C.push_back(next.B.back());
				next.B.pop_back();
				++next.step;
				que.push(next);
			}
			if (!now.C.empty() && (now.B.empty() || (now.B.back() < now.C.back()))){
				state next = now;
				next.B.push_back(next.C.back());
				next.C.pop_back();
				++next.step;
				que.push(next);
			}

		}

	}
	return 0;
}

