#include <bits/stdc++.h>
using namespace std;

using pii = pair<int, int>;
using vi = vector<int>;
using ll = long long;

int mypow(int x, int n) {
    int res = 1;
    while(n > 0) {
        if(n & 1) {
            res *= x;
        }
        x *= x;
        n >>= 1;
    }
    return res;
}

int calc(vector<int> const& v) {
    int res = 0;
    for(int i = 1; i < 3; ++i) {
        for(int j = 0; j < 15; ++j) {
            if(v[i] & (1 << j)) {
                res += i * mypow(3, j);
            }
        }
    }
    return res;
}

bool memo[30000000] = {};

int main() {
    int n, m;
    while(cin >> n >> m, n) {
        vi s(3);
        for(int i = 0; i < 3; ++i) {
            int k; cin >> k;
            for(int j = 0; j < k; ++j) {
                int x; cin >> x;
                s[i] |= (1 << x - 1);
            }
        }
        fill(begin(memo), end(memo), false);
        memo[calc(s)] = true;
        queue<pair<vi, int>> que;
        que.emplace(move(s), 0);
        int res = -1;
        while(!que.empty()) {
            auto now = move(que.front().first);
            int step = que.front().second;
            que.pop();
            if(step > m) {
                continue;
            }
            if(now[0] == (1 << n) - 1 || now[2] == (1 << n) - 1) {
                res = step;
                break;
            }
            for(int i = 0; i < 3; ++i) {
                if(now[i] == 0) {
                    continue;
                }
                for(int j = i - 1; j <= i + 1; j += 2) {
                    if(j < 0 || 3 <= j) {
                        continue;
                    }
                    int xi = (31 - __builtin_clz(now[i]));
                    if(now[j] == 0 || (31 - __builtin_clz(now[j])) < xi) {
                        now[j] |= (1 << xi);
                        now[i] ^= (1 << xi);
                        if(!memo[calc(now)]) {
                            memo[calc(now)] = true;
                            que.emplace(now, step + 1);
                        }
                        now[i] |= (1 << xi);
                        now[j] ^= (1 << xi);
                    }
                }
            }
        }
        cout << res << endl;
    }
}

