#define MAX 16
#define A 0
#define B 1
#define C 2
#define P_MAX 3
#define M_MAX 16000000

using namespace std;

template<typename _T>
_T &abs(const _T &x){if(x<0)return -x;else return x;}

int cost[3][MAX];

struct Cups{
	Cups(){
		cost = 0;
		v[0].reserve(MAX);
		v[1].reserve(MAX);
		v[2].reserve(MAX);
	}
	vector<int> v[P_MAX];
	int cost;
};
ostream &operator<<(ostream &os, const vector<int> &v){
	for(unsigned int i = 0; i < v.size(); ++i){
		os << v[i] << ' ';
	}
	return os;
}
ostream & operator << (ostream &os, const Cups &t){
	os << "A: " << t.v[0] << "\t\tB: " << t.v[1] << "\t\tC: " << t.v[2] << "\t\tCost :" << t.cost;
	return os;
}

int BFS(int n, int m, Cups &st){
	int ret = M_MAX;
	queue<Cups> q;
	q.push( st );

	while( !q.empty() ){
		Cups cups;
		cups = q.front();

		q.pop();

		/* I */
		if( cups.v[A].size() == n || cups.v[C].size() == n ){
			ret = min(ret, cups.cost);
			continue;
		}

		if( cups.cost >= ret || cups.cost > m )
			continue;

		for(int i = A; i <= C; ++i){
			int mov_cnt = 1;
			int min_sz = -1;

			for(int j = cups.v[i].size()-1; j >= 1; --j){
				if( cups.v[i][j] - cups.v[i][j-1] == 1 ){
					++mov_cnt;
					min_sz = cups.v[i][j-1];
				}else{
					min_sz = cups.v[i][j];
					break;
				}
			}
			if( cups.v[i].size() == 1 )
				min_sz = cups.v[i][ 0 ];

			if( min_sz == -1 )
				continue;

			for(int w = 1; w <= mov_cnt; ++w){
				for(int j = A; j <= C; ++j){
					int d = abs( i - j );
					if( d == 2 || d == 0 )
						continue;

					Cups t = cups;
					int end = t.v[j].size() - 1;
					if( end >= 0 && t.v[j][end] > min_sz )
						break;

