#include <iostream>
#include <vector>

using namespace std;

vector<int> cups;

const int LEFT = 0;
const int CENTER = 1;
const int RIGHT = 2;

bool isMovement(int deepness, int deepest, int tray) {
	int pos = cups[deepness];
	int oppositePos = RIGHT - tray;
	if (pos == tray)
		return true;
	for (int i = deepness+1; i < deepest; i++) {
		int bigCupPos = cups[deepness];
		if (bigCupPos == tray) {
			return false;
		} else if (pos == oppositePos && bigCupPos != tray) {
			return false;
		} else if (pos == CENTER && bigCupPos == CENTER) {
			return false;
		}
	}
	return true;
}

int sortCup(int deepness, int deepest, int tray) {
	int pos = cups[deepness];
	int oppositePos = RIGHT - tray;
	int ret = 0;

	if (isMovement(deepness, deepest, tray) == true) {
		if (pos == oppositePos) {
			ret += 2;
		} else if (pos == CENTER) {
			ret += 1;
		}
		cups[deepness] = tray;
		return ret;
	}

	if (pos == oppositePos) {
		for (int i = deepness+1; i < deepest; i++) {
			ret += sortCup(i, deepest, tray);
		}
		pos = cups[deepness] = CENTER;
		ret++;
	}

	for (int i = deepness+1; i < deepest; i++) {
		ret += sortCup(i,  deepest, oppositePos);
	}
	ret++;
	cups[deepness] = tray;

	return ret;
}

int main(void) {
	int n = 0, m = 0;
	vector<int> answers;
	while ((cin >> n >> m) && n != 0 && m != 0) {
		cups.clear();
		for (int i = 0; i < n; i++) {
			cups.push_back(0);
		}

		int trayCup = 0;
		for (int j = 0; j < 3; j++) {
			cin >> trayCup;
			for (int i = 0; i < trayCup; i++) {
				int tmpCupSize = 0;
				cin >> tmpCupSize;
				cups[tmpCupSize-1] = j;
			}
		}
		int answerLeft = 0;
		vector<int> tmpCup(cups);
		for (int i = 0; i < n; i++) {
			answerLeft += sortCup(i, n, LEFT);
		}
		cups.swap(tmpCup);
		int answerRight = 0;
		for (int i = 0; i < n; i++) {
			answerRight += sortCup(i, n, RIGHT);
		}
		int answerMin = min(answerLeft, answerRight);
		if (answerMin <= m) {
			answers.push_back(answerMin);
		} else {
			answers.push_back(-1);
		}
	}

	for (int i = 0; i < answers.size(); i++) {
		cout << answers[i] << endl;
	}

	int i = 0;
	cin >> i;
	return 0;
}

