map<vector<set<int> >,int> dpp;

void dfs(vector<set<int> > sit,int n,int cnt,int m){
	if(dpp.find(sit) != dpp.end()){
		if(dpp[sit] <= cnt){
			return;
		}
		else{
			dpp[sit] = cnt;
		}
	}
	else{
		dpp[sit] = cnt;
	}

	if(cnt >= m)
		return;

	if((sit[0].size() == 0 && sit[1].size() == 0) || (sit[1].size() == 0 && sit[2].size() == 0)){
		return;
	}

	// A->B
	vector<set<int> > tmp = sit;
	if(sit[0].size() != 0){
		set<int>::reverse_iterator it = sit[0].rbegin();
		int cont = *it;
		if(sit[1].size()==0 || *(sit[1].rbegin()) < cont){
			sit[0].erase(*it);
			sit[1].insert(cont);
			dfs(sit,n,cnt+1,m);
		}
	}
	// B->A
	sit = tmp;
	if(sit[1].size() != 0){
		set<int>::reverse_iterator it = sit[1].rbegin();
		int cont = *it;
		if(sit[0].size() == 0 || *(sit[0].rbegin()) < cont){
			sit[1].erase(*it);
			sit[0].insert(cont);
			dfs(sit,n,cnt+1,m);
		}
	}
	// B->C
	sit = tmp;
	if(sit[1].size() != 0){
		set<int>::reverse_iterator it = sit[1].rbegin();
		int cont = *it;
		if(sit[2].size() == 0 || *(sit[2].rbegin()) < cont){
			sit[1].erase(*it);
			sit[2].insert(cont);
			dfs(sit,n,cnt+1,m);
		}
	}
	// C->B
	sit = tmp;
	if(sit[2].size() != 0){
		set<int>::reverse_iterator it = sit[2].rbegin();
		int cont = *it;
		if(sit[1].size() == 0 || *(sit[1].rbegin()) < cont){
			sit[2].erase(*it);
			sit[1].insert(cont);
			dfs(sit,n,cnt+1,m);
		}
	}
}

