int memo[15000000];

int encode(vector<vint> state) {
    int res = 0;
    vint num(n, 0);
    for (int i = 0; i < state.size(); ++i) {
        for (int j = 0; j < state[i].size(); ++j) {
            int a = state[i][j];
            num[a] = i;
        }
    }
    for (int i = n-1; i >= 0; --i) {
        res += num[i];
        if (i != 0) res *= 3;
    }
//    COUT(state);
//    COUT(num);
//    COUT(res);
    return res;
}

vector<vint> decode(int num) {
    vector<vint> res(3);
    for (int i = 0; i < n; ++i) {
        int a = num % 3;
        res[a].PB(i);
        num /= 3;
    }
    return res;
}

