bool use[15000000];

int rec(int time){
  int res = -1,tmp,val;
  if((int)s[0].size()==n || (int)s[2].size()==n)return time;
  if(time>m)return -1;
  int d1[] = {0,1,1,2},d2[] = {1,0,2,1};

  for(int i=0;i<4;i++){
    // cout << time << " " << i << " " << s[0].size() << " " << s[1].size() << endl;
    if(s[d1[i]].size() && (s[d2[i]].empty() || s[d1[i]].top()>s[d2[i]].top())){
      tmp = s[d1[i]].top();
      int hoge = pow(3,tmp-1);
      now -= hoge*d1[i] - hoge*d2[i];
      //cout << now << endl;
      if(!use[now]){
	use[now] = true;
	s[d1[i]].pop();
	s[d2[i]].push(tmp);

	val = rec(time+1);
	if(res<0)res = val;
	else if(val>=0)res = min(res,val);

	s[d2[i]].pop();
	s[d1[i]].push(tmp);
	use[now] = false;
      }
      now += hoge*d1[i] - hoge*d2[i];
    }
  }
 return res;
}

