struct P
{
	vector<int> mem[3];
	ll point;
};
int n, m;
int vis[15000000] = {-1};
queue<P> que; 

ll cal_point(P p){
	ll point = 0; 
	rep(i,3){
		rep(j,p.mem[i].size()){
			point += i * pow(3,p.mem[i][j]-1);
		}
	}
	return point;
}

void INIT(){
	memset(vis,-1,sizeof(vis));
	while(!que.empty()){
		que.pop();
	}
}

P Change(P p, int num1, int num2){
	int l = p.mem[num1].size()-1;
	int tmp = p.mem[num1][l];
	p.mem[num1].pop_back();
	p.mem[num2].push_back(tmp);
	return p;
}

