}

int BFS(){
	int step = 0;
	while(!que.empty()){
		P p = que.front(); que.pop();

		if(vis[p.point]>m){
			step = -1;
			break;
		}
		//cout << "point is " << p.point << "; step is " << vis[p.point] << endl;//
		//PRINT(p);

		if(p.mem[1].size()==0 && 
			(p.mem[0].size()==0 || p.mem[2].size()==0)){
			step = vis[p.point];
			break;
		}

		int L[3]; 
		rep(i,3) L[i] = p.mem[i].size()-1;

		rep(i,3) rep(j,3){ // from L[i] to L[j]
			if(abs(i-j)!=1) continue;
			if(L[i]>=0){ // from 
				int ok = 0;
				if(L[j]>=0){ // to
					if(p.mem[i][L[i]] >= p.mem[j][L[j]]) ok = 1;
				}
				else if(p.mem[j].size() == 0) ok = 1;

				if(ok){
					P np = Change(p,i,j); //
					np.point = cal_point(np);
					if(vis[np.point]==-1){
						vis[np.point] = vis[p.point]+1;
						que.push(np);
					} 
				}
			}
		}
	}
	//cout << "!!"  << endl;
	return step;
}

