#include<stdio.h>
int d[3][16];
int x;
int i,j,k;
int m,n;
int ans=0;
int goal;
int now[3];
int p,q;

int other(int a,int b){
	if(a==0&&b==1)return 2;
	if(b==0&&a==1)return 2;
	if(a==0&&b==2)return 1;
	if(b==0&&a==2)return 1;
	if(a==1&&b==2)return 0;
	if(b==1&&a==2)return 0;
}

void move(int n,int a,int b,int to){
start:
	if(ans>m)return;
	if(n!=d[a][b])return;
	if(to-a==2||a-to==2){
		move(d[a][b],a,b,1);
		move(d[1][now[1]-1],1,now[1]-1,to);
		if(now[a]==0)return;
		if(d[a][b]==-1)b=now[a]-1;
		goto start;
	}
	if(d[a][b+1]>0){
		move(d[a][b+1],a,b+1,other(a,to));
		if(now[a]==0)return;
		if(d[a][b]==-1)b=now[a]-1;
		goto start;
	}
	if(now[to]>=1&&d[to][now[to]-1]>d[a][b]){
		move(d[to][now[to]-1],to,now[to]-1,other(a,to));
		if(now[a]==0)return;
		if(d[a][b]==-1)b=now[a]-1;
		goto start;
	}
	d[to][now[to]++]=d[a][b];
	d[a][b]=-1;
	now[a]--;
	ans++;
}

