			if(i==0){
				if(d[1][0]==1){
					if(d[2][0]>d[0][0]){
						if(now[0]==0)goal=0;
						else goal=2;
					}
					else{
						if(now[2]==0)goal=2;
						else goal=0;
					}
				}
				else if(d[0][0]==1)goal=0;
				else goal=2;
			}
			p=-1;q=-1;
			for(j=0;j<3;j++)for(k=0;k<16;k++)if(d[j][k]==i+1){p=j;q=k;}
			if(p!=-1&&q!=-1)if(p!=goal)move(i+1,p,q,goal);
			if(ans>m)break;
		}
