int n, m;

int solve(vector<int>& v, int x) {
	int ret = 0;
	for (int i = 0; i < n;i++) {
		if (v[i] == 1) {
			ret += pow(3, n - 1 - i);
			x ^= 2;
		}
		else if (v[i] != x) {
			ret += 2 * pow(3, n - 1 - i);
		}
	}
	return ret;
}

