			int c; cin >> c;
			for (int j = 0; j < c; j++) { int x; cin >> x; v[x - 1] = i; }
		}
		int ans = min(solve(v, 0), solve(v, 2));
		cout << ((ans > m) ? -1 : ans) << endl;
	}
}

