int n, m, cnt, a[16], b[16];
bool flag;

void move(int num, int dest, int* a);

int main() {

	while (1) {
		cin >> n >> m;
		if (n == 0 && m == 0)break;

		int numt, tmp;
		for (int i = 0; i < 3; i++) {
			cin >> numt;
			for (int j = 0; j < numt; j++) {
				cin >> tmp;
				a[tmp] = i;
				b[tmp] = i;
			}
		}

		int ans1, ans2;

		cnt = 0;
		flag = true;
		for (int num = 1; num <= n; num++) {
			move(num, 2, a);
		}
		if (flag == true) {
			ans1 = cnt;
		} else {
			ans1 = -1;
		}

		cnt = 0;
		flag = true;
		for (int num = 1; num <= n; num++) {
			move(num, 0, b);
		}
		if (flag == true) {
			ans2 = cnt;
		} else {
			ans2 = -1;
		}


		int ans;
		if (ans1 == -1) {
			ans = ans2;
		} else if(ans2 == -1){
			ans = ans1;
		} else {
			ans = min(ans1, ans2);
		}
		cout << ans1 << " " << ans2 << endl;
		cout << ans << endl;
	}


	return 0;
}

void move(int num, int dest, int* a) {

	if (cnt > m || flag == false) {
		flag = false;
		return;
	}

	int dist = abs(dest - a[num]);
	if (dist == 0) {
		return;
	} else if (dist == 1) {
		if (num == n) {
			cnt += 1;
			a[num] = dest;
		} else {
			int i = 1;
			while (num + i <= n) {
				move(num + i, 3 - dest - a[num], a);
				i++;
			}
			cnt += 1;
			a[num] = dest;
		}
	} else if (dist == 2) {
		if (num == n) {
			cnt += 2;
			a[num] = dest;
		} else {
			move(num, 1, a);
			move(num, dest, a);
		}
	}


	return;
}

