static const int P[16] = { 1, 3, 9, 27, 81, 243, 729, 2187, 6561, 19683, 59049,
              177147, 531441, 1594323, 4782969, 14348907};
int n, m, d[14348907];

int getA(int state){
    for(int i = n - 1; i >= 0; i--)
        if((state % P[i + 1]) / P[i] == 0) return i + 1;
    return 0;
}

int getB(int state){
    for(int i = n - 1; i >= 0; i--)
        if((state % P[i + 1]) / P[i] == 1) return i + 1;
    return 0;
}

int getC(int state){
    for(int i = n - 1; i >= 0; i--)
        if((state % P[i + 1]) / P[i] == 2) return i + 1;
    return 0;
}

int bfs(int state){
    fill(d, d + 14348907, -1);
    d[state] = 0;
    queue<int> todo;
    todo.push(state);

    while(!todo.empty()){
        int state = todo.front();
        todo.pop();
        if(d[state] == m + 1) return -1;
        if(state == 0 || state == P[n] - 1) return d[state];
        int a = getA(state), b = getB(state), c = getC(state);
        if(a != 0 && a > b){
            int nstate = state + P[a - 1];
            if(d[nstate] == -1){
                d[nstate] = d[state] + 1;
                todo.push(nstate);
            }
        }
        if(b != 0 && a < b){
            int nstate = state - P[b - 1];
            if(d[nstate] == -1){
                d[nstate] = d[state] + 1;
                todo.push(nstate);
            }
        }
        if(b != 0 && b > c){
            int nstate = state + P[b - 1];
            if(d[nstate] == -1){
                d[nstate] = d[state] + 1;
                todo.push(nstate);
            }
        }
        if(c != 0 && b < c){
            int nstate = state - P[c - 1];
            if(d[nstate] == -1){
                d[nstate] = d[state] + 1;
                todo.push(nstate);
            }
        }
    }
    return -1;
}

