unsigned f(unsigned S, int x, int from, int to) {
  if (from >= 0) {
    S &= ~((from >> 0 & 1) << (x << 1));
    S &= ~((from >> 1 & 1) << ((x << 1) + 1));
  }
  S |= (to >> 0 & 1) << (x << 1);
  S |= (to >> 1 & 1) << ((x << 1) + 1);
  return S;
}

vector<int> getTop(unsigned S, int n) {
  vector<int> res(3, -1);
  for (int i = 0; i < n; i++) {
    int l = S >> ((i << 1) + 1) & 1;
    int r = S >> (i << 1) & 1;
    res[(l << 1) + r] = max(res[(l << 1) + r], i);
  }
  return res;
}

