      for (int i = 0; i < 3; i++) {
        int l = i == 0 ? -1 : i - 1;
        int r = i == 2 ? -1 : i + 1;
        if (l >= 0 && v[i] > v[l]) {
          ns = f(s, v[i], i, l);
          if (d.find(ns) == d.end()) {
            d[ns] = d[s] + 1;
