int func(int a, int b, int c) {
    if (b == 0 && c == 0)
        return 0;
    else if (a & 1)
        return func(a >> 1, b >> 1, c >> 1);
    else if (b & 1)
        return func(c >> 1, b >> 1, a >> 1) + 1 + func(0, 0, (a | b | c) >> 1);
    else
        return func(a >> 1, b >> 1, c >> 1) + 2 +
               4 * func(0, (a | b | c) >> 1, 0);
}

