#define INF 100000000
int m;
int tr[3][15];
int otr[3][15];
int ctr[3];
int octr[3];
int p[15];
int d[15];
int op[15];
int od[15];
bool ng=false;
int min(int a,int b){return a<b?a:b;}
int max(int a,int b){return a>b?a:b;}
int other(int a,int b){
	int p,q;
	p=min(a,b);
	q=max(a,b);
	if(p==0&&q==1)return 2;
	else if(p==0&&q==2)return 1;
	else return 0;
}
int solve(int from,int dep,int to){
	if(from==to)return 0;
	if(abs(from-to)==2){
		return solve(from,dep,1)+solve(1,ctr[1]-1,to);
	}
	if(ng)return INF;
	bool f=true;
	int t=0;
	while(f){
		if(ng)return INF;
		f=false;
		if(dep!=ctr[from]-1){
			f=true;
			t+=solve(from,dep+1,other(from,to));
		}
		if(tr[to][ctr[to]-1]>tr[from][dep]){
			f=true;
			t+=solve(to,ctr[to]-1,other(from,to));
		}
	}
	tr[to][ctr[to]++]=tr[from][dep];
	ctr[from]--;
	p[tr[from][dep]-1]=to;
	d[tr[from][dep]-1]=ctr[to]-1;
	t++;
	if(t>=m){
		ng=true;
		return INF;
	}
	return t;
}

