			d[tr[i][ctr[i]-1]-1]=ctr[i]-1;
		}
	}
	int ans;
	int res=0;
	for(i=0;i<3;i++){
		octr[i]=ctr[i];
		for(j=0;j<15;j++)otr[i][j]=tr[i][j];
	}
	for(i=0;i<n;i++){
		op[i]=p[i];
		od[i]=d[i];
	}
