// 
int top(int bits){
	for(int i=(1<<(n-1)) ; i ; i >>= 1 ){
		if( bits & i ) return i;
	}
	return 0;
}

bool is_goal(bitset<16> a, bitset<16> b, bitset<16> c){
	return ( b == cup[1] && ((a == cup[0] && c == cup[2]) || (a == cup[2] && c == cup[0])) );
}

// 
int bfs(){
	// d[P(a,b)] := cup[0] = a, cup[1] = b  s 
	// (cup[2] = (2^n)-1-a-b  cup[2] )
	map<P,int> d;
	queue<P> q;

	P s = P( bitset<16>() , bitset<16>() );
	q.push(s);
	d[s] = 0;

	while( !q.empty() ){
		P now = q.front();
		int cnt = d[now];
		q.pop();

		// a,b,c := 
		// a_top,b_top,c_top := 
		int a = now.first.to_ulong();
		int b = now.second.to_ulong();
		int c = ((1<<n)-1) - a - b;
		int a_top = top(a);
		int b_top = top(b);
		int c_top = top(c);

		//debug(a,b,c,cnt);

		// 
		if( is_goal(now.first,now.second,bitset<16>(c)) ){
			return cnt;
		}

		{
			int a_, b_, c_;
			if( a_top < b_top ){ // B -> A 
				a_ = a + b_top;
				b_ = b - b_top;
				c_ = c_;
			}else{ // A -> B 
				a_ = a - a_top;
				b_ = b + a_top;
				c_ = c;
			}
			P next = P( bitset<16>(a_) , bitset<16>(b_) );
			if( !d.count(next) && cnt + 1 <= m ){
				d[next] = cnt + 1;
				q.push(next);
			}
		}
		{
			int a_, b_, c_;
			if( c_top < b_top ){ // B -> C 
				a_ = a;
				b_ = b - b_top;
				c_ = c + b_top;
			}else{ // C -> B 
				a_ = a;
				b_ = b + c_top;
				c_ = c - c_top;
			}
			P next = P( bitset<16>(a_) , bitset<16>(b_) );
			if( !d.count(next) && cnt + 1 <= m ){
				d[next] = cnt + 1;
				q.push(next);
			}
		}
	}
	return -1;
}

