            rep(i,n){
                int i2 = n-i-1;
                rep(j,3){
                    if(tray[j] == true && cup[i2] == j){
                        tray[j] = false;
                        bigger[maxcount] = i2;
                        maxcount++;
                        break;
                    }
                }
                if(maxcount == 3)break;
            }
            int movecount = mp[cup] + 1;
            if(movecount > m) break;
            if(cup[bigger[0]]-1 >= 0){
                cup[bigger[0]]--;
                if(mp.count(cup) == 0){
                    mp[cup] = movecount;
                    que.push(cup);
                }
                cup[bigger[0]]++;
            }
            if(cup[bigger[0]]+1 <= 2){
                cup[bigger[0]]++;
                if(mp.count(cup) == 0){
                    mp[cup] = movecount;
                    que.push(cup);
                }
                cup[bigger[0]]--;
            }
            if(maxcount >= 2){
                if(cup[bigger[1]]-1 >= 0 && cup[bigger[1]]-1 != cup[bigger[0]]){
                    cup[bigger[1]]--;
                    if(mp.count(cup) == 0){
                        mp[cup] = movecount;
                        que.push(cup);
                    }
                    cup[bigger[1]]++;
                }
                else if(cup[bigger[1]]+1 <= 2 && cup[bigger[1]]+1 != cup[bigger[0]]){
                    cup[bigger[1]]++;
                    if(mp.count(cup) == 0){
                        mp[cup] = movecount;
                        que.push(cup);
                    }
                    cup[bigger[1]]--;
                }
            }
        }
        if(flag) cout << "-1" << endl;
        vector<int>().swap(cup);
        mp.clear();
        while(que.size()!=0)que.pop();
    }
    return 0;
}

#include <cstdio>
#include <algorithm>
using namespace std;

int tostate(int a, int b, int c, int k)
{
	int mul;
	int ret = 0;

	for(int i = 0, mul = 1; i < k; ++i, mul *= 3)
		ret += ((a >> i) & 1) * 1 * mul;
	for(int i = 0, mul = 1; i < k; ++i, mul *= 3)
		ret += ((b >> i) & 1) * 2 * mul;

	return ret;
}

inline int mhb(int x)
{
	for(int i = 31; i >= 0; --i) {
		if(x & (1 << i))
			return 1 << i;
	}

	return 0;
}

const int inf = 1000000000;
const int pow3x5 = 3 * 3 * 3 * 3 * 3;
const int pow3x15 = pow3x5 * pow3x5 * pow3x5;
bool visit[pow3x15];

int check(int a, int b, int c, int remain, int k)
{
	if(remain < 0)
		return inf;

	if(b == 0 && (a == 0 || c == 0))
		return remain;

	int state = tostate(a, b, c, k);

	if(visit[state])
		return inf;
	visit[state] = true;

	int ret = inf;

	if(a > b) {
		int move = mhb(a);
		int ta = a - move;
		int tb = b + move;
		ret = min(ret, check(ta, tb, c, remain - 1, k));
	} else {
		int move = mhb(b);
		int ta = a + move;
		int tb = b - move;
		ret = min(ret, check(ta, tb, c, remain - 1, k));
	}

	if(b > c) {
		int move = mhb(b);
		int tb = b - move;
		int tc = c + move;
		ret = min(ret, check(a, tb, tc, remain - 1, k));
	} else {
		int move = mhb(c);
		int tb = b + move;
		int tc = c - move;
		ret = min(ret, check(a, tb, tc, remain - 1, k));
	}

	return ret;
}

int main()
{
	while(true) {

		int n, m;

		scanf("%d%d", &n, &m);
		if(n == 0)
			break;

		fill(visit + 0, visit + pow3x15, false);

		int a[3];
		int bita[3] = {0};
		int k = 0;

		for(int i = 0; i < 3; ++i) {
			scanf("%d", a + i);
			for(int j = 0; j < a[i]; ++j) {
				int t;
				scanf("%d", &t);
				bita[i] += 1 << (t - 1);
				k = max(k, t);
			}
		}

		int ans = m - check(bita[0], bita[1], bita[2], m, k);

		printf("%d\n", ans >= 0 ? ans : -1);
	}

	return 0;
}

