#define INF (1<<28)

using namespace std;

int n, m;
stack<int> p[3];

#define AB 1
#define BC 3

int move(int from, int to, int prev) {
    int c = p[from].top();
    p[to].push(c);
    p[from].pop();
    if (p[1].top() == 0 && (p[0].top() == 0 || p[2].top() == 0)) return 1;
    if (prev == BC) {
        if (p[0].top() > p[1].top()) return move(0, 1, AB) + 1;
        else if (p[0].top() < p[1].top()) return move(1, 0, AB) + 1;
        return INF;
    } else if (prev == AB) {
        if (p[1].top() > p[2].top()) return move(1, 2, BC) + 1;
        else if (p[1].top() < p[2].top()) return move(2, 1, BC) + 1;
        return INF;
    }
}

int solve() {
    if (p[1].top() == 0 && (p[0].top() == 0 || p[2].top() == 0)) return 0;
    int ans = INF;
    stack<int> backup[3];
    for (int i = 0; i < 3; i++) backup[i] = p[i];

    if (p[0].top() > p[1].top()) {
        ans = min(ans, move(0, 1, AB));
        for (int i = 0; i < 3; i++) p[i] = backup[i];
    } else {
        ans = min(ans, move(1, 0, AB));
        for (int i = 0; i < 3; i++) p[i] = backup[i];
    }
    if (p[1].top() > p[2].top()) {
        ans = min(ans, move(1, 2, BC));
        for (int i = 0; i < 3; i++) p[i] = backup[i];
    } else {
        ans = min(ans, move(2, 1, BC));
        for (int i = 0; i < 3; i++) p[i] = backup[i];
    }
    return ans;
}

int main() {
    while (cin >> n >> m, n || m) {
        for (int i = 0; i < 3; i++) {
            p[i] = stack<int>();
            p[i].push(0);
            int s; cin >> s;
            for (int j = 0; j < s; j++) {
                int c; cin >> c;
                p[i].push(c);
            }
        }
        int step = solve();
        cout << (step > m ? -1 : step) << endl;
    }
    return 0;
}

