			cin >> num;
			for (int j = 0; j < num; j++) {
				cin >> tmp;
				a[tmp] = i;
				b[tmp] = i;
			}
		}

		cnt = 0;
		for (int num = 1; num <= n; num++) {
			move(num, 2, a);
