		for (int num = 1; num <= n; num++) {
			move(num, 0, b);
		}
		int ans2 = cnt;

		int ans;
		if (ans1 == -1) {
			ans = ans2;
		} else if(ans2 == -1){
			ans = ans1;
		} else {
			ans = min(ans1, ans2);
		}
		cout << ans << endl << endl;
	}


	return 0;
}

void move(int num, int dest, int* a) {

	if (cnt > m || cnt == -1) {
		cnt = -1;
		return;
	}

	int dist = abs(dest - a[num]);
	if (dist == 0) {
		return;
	} else if (dist == 1) {
		if (num == n) {
			cnt += 1;
			a[num] = dest;
		} else {
			move(num + 1, 3 - dest - a[num], a);
			cnt += 1;
			a[num] = dest;
		}
	} else if (dist == 2) {
		if (num == n) {
			cnt += 2;
			a[num] = dest;
		} else {
			move(num, 1, a);
			move(num, dest, a);
		}
	}


	return;
}

