int solve() {
    int P[17];
    P[n] = 2;
    for ( int i = n; i > 0; -- i ) P[i-1] = 3 * P[i] + 2;

    A[n] = T[n];
    B[n] = 2 - T[n];
    for ( int i = n; i > 1; -- i ) {
        if ( T[i-1] == 0 ) {
            A[i-1] = A[i];
            B[i-1] = A[i] + 2 * P[i] + 2;
        } else if ( T[i-1] == 1 ) {
            A[i-1] = B[i] + P[i] + 1;
            B[i-1] = A[i] + P[i] + 1;
        } else if ( T[i-1] == 2 ) {
            A[i-1] = A[i] + 2 * P[i] + 2;
            B[i-1] = B[i];
        }
    }
    int res = min( A[1], B[1] );
    if ( res > m ) return -1;
