int times(int A, int B, int C, int target)
{
    //printf("%d:%d:%d\n", A, B, C);
    if( B & 1 )
        return std::min(times(A>>1,B>>1,C>>1,-1), times(A>>1,B>>1,C>>1,1)) + 1 + times(((A|B|C)>>1) , 0, 0, 1);
    if( A & 1 )
        if( target == -1 )
            return times(A>>1, B>>1, C>>1, -1);
        else
            return times(A>>1, B>>1, C>>1, 1) + 1 + times(0, 0, ((A|B|C)>>1) , -1) + 1 + times(((A|B|C)>>1) , 0, 0, 1);
    if( C & 1 )
        if( target == 1 )
            return times(A>>1, B>>1, C>>1, 1);
        else
            return times(A>>1, B>>1, C>>1, -1) + 1 + times(((A|B|C)>>1), 0, 0, 1) + 1 + times(0, 0, ((A|B|C)>>1), -1);
    return 0;
}



