class hash_array {
public:
  size_t operator()(const array<int, 3> &x) const {
    const int C = 997;
    size_t t = 0;
    for (int i = 0; i != x.size(); ++i) {
      t = t * C + x[i];
    }
    return t;
  }
};

int solve(array<int, 3> const& start) {
  queue<array<int, 3>> q;
  int num = 0;
  rep(i, N) num |= 1 << i;
  q.push(start);

  unordered_map<array<int, 3>, int, hash_array> mp;
  mp[start] = 0;

  const array<int, 3> target1{{num, 0, 0}};
  const array<int, 3> target2{{0, 0, num}};

  while(!q.empty()) {
    auto v = q.front(); q.pop();
    if(v == target1 || v == target2) {
      return mp[v];
    }

    rep(i, v.size()) {
      for(int k=N-1; k>=0; k--) {
        if(v[i] >> k & 1) {
          auto& rmp = mp[v];
          if(i + 1 < 3) {
            v[i] ^= 1 << k;
            if(v[i + 1] < (1<<k)) {
              v[i + 1] ^= 1 << k;
              if(mp.find(v) == mp.end() && rmp + 1 <= M) {
                mp[v] = rmp + 1;
                q.push(v);
              }
              v[i + 1] ^= 1 << k;
            }
            v[i] ^= 1 << k;
          }

          if(i - 1 >= 0) {
            v[i] ^= 1 << k;
            if(v[i - 1] < (1<<k)) {
              v[i - 1] ^= 1 << k;
              if(mp.find(v) == mp.end() && rmp + 1 <= M) {
                mp[v] = rmp + 1;
                q.push(v);
              }
              v[i - 1] ^= 1 << k;
            }
            v[i] ^= 1 << k;
          }

          break;
        }
      }
    }
  }

  return -1;
}

