int judge(int max, int temp){
	if(temp + 1 == an + bn + cn)return -1;
	if(a[an] == max - temp)return 50 + temp;
	if(c[cn] == max - temp)return 100 + temp;
	if(b[cn - temp] == max - temp)return judge(max, temp + 1);
	return 0;
}

int calculation(int min, int max){
	int sum;
	if(min == max)return 0;
	if(min < max)sum = calculation(++min, max) * 3 + 2;
	return sum;
}

int move(int flag){
	int number;
	if(flag == 2){  //right
		number = an - m1.nu + 1;
		for(int i = 1;i <= number;i++)c[++cn] = a[an--];
	}
	if(flag == 1){
		number = cn - m1.nu + 1;
		for(int i = 1;i <= number;i++)a[++an] = c[cn--];
	}
	total += calculation(0, number);
	//printf("*%d*", number);
	return 0;
}

int sort(int max){
	if(m1.ch == m2.ch)return 0;
	if(m2.ch == 'b'){
		if(m1.ch == 'a')c[++cn] = b[bn--], move(2);  //right
		if(m1.ch == 'c')a[++an] = b[bn--], move(1);
		total++;
	}else if(m2.ch == 'a'){
		if(c[cn - 2] == max - 2){
			b[++bn] = a[an--], move(1);
			c[++cn] = b[bn--], move(2);
		}else{
			move(1);
		}
	}else if(m2.ch == 'c'){
		if(a[an - 2] == max - 2){
			b[++bn] = c[cn--], move(2);
			a[++an] = b[bn--], move(1);
		}else{
			move(2);
		}
	}
	return 0;
}

int solve(int max){
	for(int i = 1;i <= an;i++){
		if(a[i] == max)m1.ch = 'a',m1.nu = i;
		if(a[i] == max - 1)m2.ch = 'a', m2.nu = i;
	}
	for(int i = 1;i <= bn;i++){
		if(b[i] == max)m1.ch = 'b',m1.nu = i;
		if(b[i] == max - 1)m2.ch = 'b', m2.nu = i;
	}
	for(int i = 1;i <= cn;i++){
		if(c[i] == max)m1.ch = 'c',m1.nu = i;
		if(c[i] == max - 1)m2.ch = 'c', m2.nu = i;
	}
	if(f == 1 && m1.ch == 'b'){
		int vec;  //1 = left 
		if(m2.ch == 'b'){
			int temp = judge(max, 2);
			if(temp == -1)return 0;
			if(temp >= 100){
				if(temp % 2)vec = 2;
				else vec = 1;
			}else if(temp >= 50){
				if(temp % 2)vec = 1;
				else vec = 2;
			}
		}

		if(m2.ch == 'a' || vec == 1){
			a[++an] = b[bn--];
		}else if(m2.ch == 'c' || vec == 2){
			c[++cn] = b[bn--];
		}
		total++;
	}else{
		sort(max);
	}
	return 0;
}

