		int n = 0;

		stack_C.pop_front();
		n = stack_A.size() + stack_B.size() + stack_C.size();
		return f_A(stack_A, stack_B, stack_C) + (int(pow(3, n)) + 1) / 2;
	}

	//std::cout << "error f_B" << std::endl;
	//exit(0);
	return 0;
}

int f_C(std::deque<char> stack_A, std::deque<char> stack_B, std::deque<char> stack_C){
