using namespace std;

class State {
public:
  stack<int> st[3];
  int cost, goal;
  State() : cost(0) {}
  void init() {
    cost = 0;
    for(int i = 0; i < 3; ++i) {
      while(!st[i].empty()) st[i].pop();
    }
  }
  bool isGoal() {
    return st[(goal+1)%3].empty() && st[(goal+2)%3].empty();
  }

  bool operator < (const State &s) const {
    if(goal != s.goal) return goal < s.goal;
    for(int i = 0; i < 3; ++i) {
      if(st[i] != s.st[i]) return st[i] < s.st[i];
    }
    return false;
  }
};

int n, m;
State S;

void solve() {
  set<State> vis;
  queue<State> que;
  S.goal = 0; vis.insert(S); que.push(S);
  S.goal = 2; vis.insert(S); que.push(S);
  while(!que.empty()) {
    State s = que.front(); que.pop();
    if(s.isGoal()) {
      cout << s.cost << endl;
      return;
    }
    if(s.cost > m) continue;
    for(int f = 0; f < 3; ++f) {
      for(int d = -1; d <= 1; d += 2) {
	if(s.st[f].empty()) continue;
	int t = f + d;
	if(t < 0 || t >= 3) continue;
	if(!s.st[t].empty() && s.st[f].top() < s.st[t].top()) continue;
	State ts = s;
	int cup = ts.st[f].top(); ts.st[f].pop();
	ts.st[t].push(cup);
	++ts.cost;
	if(vis.find(ts) != vis.end()) continue;
	vis.insert(ts);
	que.push(ts);
      }
    }
  }
  cout << -1 << endl;
}

main() {
  while((cin >> n >> m) && (n ||m)) {
    S.init();
    for(int i = 0; i < 3; ++i) {
      int num;
      cin >> num;
      for(int j = 0; j < num; ++j) {
	int a;
	cin >> a;
	S.st[i].push(a);
      }
    }
    solve();
  }
}

