int s[15],t[15],N,M;
int F(int n,int A)
{
	if(n>=N)return 0;
	int r;
	if(s[n]==A)r=F(n+1,A);
	else if(s[n]==1)
	{
		r=F(n+1,-A+2);
		r+=1+F(n+1,A);
	}
	else
	{
		r=F(n+1,A)+1;
		r+=F(n+1,-A+2)+1;
		r+=F(n+1,A);
	}
	s[n]=A;
	return r;
}
int main()
{
	int i,j,x,y,c;
	while(scanf("%d%d",&N,&M),N)
	{
		for(i=0;i<3;++i)for(j=!scanf("%d",&c);j<c;++j)
		{
			scanf("%d",&x);
			s[x-1]=t[x-1]=i;
		}
		x=F(0,0);
		memcpy(s,t,sizeof(s));
		y=F(0,2);
		x=x<y?x:y;
		if(x>=M)x=-1;
		printf("%d\n",x);
	}
	return 0;
}

