#define loop(i,a,b) for(int i=(a); i<(int)(b); i++)
#define rep(i,b) loop(i,0,b)

namespace std {
    template<typename T> bool operator<(const stack<T>& a,const stack<T>& b){
        if(a.size()!=b.size()) return a.size()<b.size();
        for(auto ta=a, tb=b; !ta.empty(); ta.pop(), tb.pop()){
            auto &ea=a.top(), &eb=b.top();
            if(ea != eb) return ea < eb;
        }
        return false;
    }
}

struct state {
    stack<int> cup[3];
    unsigned d;
    stack<int>& operator[](int i){
        return cup[i];
    }
    bool able(int f, int t){
        return cup[f].size()>0 &&
            (cup[t].size()==0 || cup[t].top()<cup[f].top());
    }
    void move(int f, int t){
        cup[t].push(cup[f].top());
        cup[f].pop();
    }
    bool operator<(const state &s)const{
        rep(i,3){
            if(cup[0]!=s.cup[0]) return cup[0]<s.cup[0];
            if(cup[1]!=s.cup[1]) return cup[1]<s.cup[1];
            if(cup[2]!=s.cup[2]) return cup[2]<s.cup[2];
        }
        return false;
    }
};

unsigned n,m;
queue<state> q;
set<state> vis;

int main(){
    while(cin>>n>>m, n||m){
        state init;
        rep(i,3){
            int k; cin>>k;
            rep(j,k){
                int t; cin>>t;
                init[i].push(t);
            }
        }
        init.d=0;
        bool able=true;
        int ans;
        q.push(init);
        while(q.size()){
            state s=q.front();
            // cout<<q.size()<<endl;
            // cout<<s<<endl;
            q.pop();
            if(vis.count(s)) continue;
            else vis.insert(s);
            if(s.d>m){
                able=false;
                break;
            }
            if(s[0].size()==n||s[2].size()==n){
                ans=s.d;
                break;
            }
            if(s.able(0,1)){
                s.move(0,1); s.d++;
                q.push(s);
                s.move(1,0); s.d--;
            }
            if(s.able(1,0)){
                s.move(1,0); s.d++;
                q.push(s);
                s.move(0,1); s.d--;
            }
            if(s.able(1,2)){
                s.move(1,2); s.d++;
                q.push(s);
                s.move(2,1); s.d--;
            }
            if(s.able(2,1)){
                s.move(2,1); s.d++;
                q.push(s);
                s.move(1,2); s.d--;
            }
        }
        cout<<(able ? ans : -1)<<endl;
    }
}

